import React from 'react';
import logo from './logo.svg';
import './App.css';
import * as firebase from "firebase/app";

// Add the Firebase services that you want to use
import "firebase/firestore";

const Livres = (props) => {
  if(props.display)
    return <div></div>;

  else 
return props.livres.map((e) => <div>e.name</div>);
}

class App extends React.Component {
  constructor() {
    super();

    var firebaseConfig = {
      apiKey: "AIzaSyB-OG_3lnqcdChpwD42uKvYWMHGZWlc4Ow",
      authDomain: "daar-a1ea7.firebaseapp.com",
      databaseURL: "https://daar-a1ea7.firebaseio.com",
      projectId: "daar-a1ea7",
      storageBucket: "daar-a1ea7.appspot.com",
      messagingSenderId: "653755445186",
      appId: "1:653755445186:web:be24541935e8161d02b05b",
      measurementId: "G-BGZWWNYW0E"
    };
    // Initialize Firebase
    firebase.initializeApp(firebaseConfig);
    firebase.analytics();

    this.state = {
      firebaseConfig: firebaseConfig,
      db: firebase.firestore(),
      livresRef: firebase.firestore().collection('livres'), 
      value: "", 
      display: false
    };
  }

  queryCustom (event) {
    var livrestmp = this.state.livresRef.where("title", "==", event.target.value);
    this.setState({livresRef: livrestmp});
  }

  handleSubmit (event) {
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">
        <h1>Bibliothèque en ligne</h1>
        <form role="search" onSubmit={()=>this.handleSubmit}>
          <input type="text" title="Recherche par mots-clés" value={this.state.value} id="rechercher" onChange={()=>this.queryCustom} />
        </form>
        <Livres livres={this.state}/>
      </div>
    );
  }
}

export default App;
